    private void jComboBox1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jComboBox1ActionPerformed
        Object[]Selected=jComboBox1.getSelectedObjects();
        int l=Selected.length;
        int total=0;
        for(int i=0; i<l; i++) {
            String Value=(String)Selected[i];
            if(Value.equals("apple"))
                total=total+80;
            else   if(Value.equals("banana"))
                total=total+60;
            else if(Value.equals("mango"))
                total=total+55;
            else if(Value.equals("guava"))
                total=total+44;
            else if(Value.equals("grapes"))
                total=total+40;
        }
        jTextField1.setText(total+"");      // TODO add your handling code here:
}//GEN-LAST:event_jComboBox1ActionPerformed

