    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        try{
            int qty=Integer.parseInt(jTextField2.getText());
            int rate=Integer.parseInt(jTextField1.getText());
            int ta=rate*qty;
            jTextField3.setText(ta+"");
        }catch (Exception e) {
            JOptionPane.showMessageDialog(null,"Please select the room and quantity"); }// TODO add your handling code here:
}//GEN-LAST:event_jButton2ActionPerformed

