    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String pwd=new String(jPasswordField1.getPassword());
        String name=jTextField1.getText();
        if(pwd.equals("tiwari")){
            JOptionPane.showMessageDialog(null,name+ ", you have logged in successfully");
            jButton1.setEnabled(false);
            jTextField1.setEnabled(false);
            jPasswordField1.setEnabled(false);
            jButton2.setEnabled(true); } else
                JOptionPane.showMessageDialog(null,name+ ", you have inserted wrong password");      // TODO add your handling code here:
}//GEN-LAST:event_jButton1ActionPerformed

